@article{orseauPolicyGuidedHeuristicSearch,
  title = {Policy-{{Guided Heuristic Search}} with {{Guarantees}}},
  author = {Orseau, Laurent and Lelis, Levi H S},
  pages = {9},
  abstract = {The use of a policy and a heuristic function for guiding search can be quite effective in adversarial problems, as demonstrated by AlphaGo and its successors, which are based on the PUCT search algorithm. While PUCT can also be used to solve singleagent deterministic problems, it lacks guarantees on its search effort and it can be computationally inefficient in practice. Combining the A* algorithm with a learned heuristic function tends to work better in these domains, but A* and its variants do not use a policy. Moreover, the purpose of using A* is to find solutions of minimum cost, while we seek instead to minimize the search loss (e.g., the number of search steps). LevinTS is guided by a policy and provides guarantees on the number of search steps that relate to the quality of the policy, but it does not make use of a heuristic function. In this work we introduce Policy-guided Heuristic Search (PHS), a novel search algorithm that uses both a heuristic function and a policy and has theoretical guarantees on the search loss that relates to both the quality of the heuristic and of the policy. We show empirically on the sliding-tile puzzle, Sokoban, and a puzzle from the commercial game ‚ÄòThe Witness‚Äô that PHS enables the rapid learning of both a policy and a heuristic function and compares favorably with A*, Weighted A*, Greedy Best-First Search, LevinTS, and PUCT in terms of number of problems solved and search time in all three domains tested.},
  langid = {english},
  file = {/home/bencic/phd/bib/zotero/storage/5WUXRGBD/Orseau and Lelis - Policy-Guided Heuristic Search with Guarantees.pdf}
}

@article{pandyLearningGraphSearch,
